import random


class UserAgentChoose:
    @classmethod
    def user_agent_random(self, ty='all'):
        """
        :param ty: all:所有端，pc，android，iphone，wechat
        :return:
        """
        if ty == 'all':
            tmp = PC_USER_AGENT + Android_USER_AGENT + iPhone_USER_AGENT + WeChat_USER_AGENT
            return random.choice(tmp)
        elif ty == 'pc':
            return random.choice(PC_USER_AGENT)
        elif ty == 'android':
            return random.choice(PC_USER_AGENT)
        elif ty == 'iphone':
            return random.choice(PC_USER_AGENT)
        elif ty == 'wechat':
            return random.choice(PC_USER_AGENT)
        else:
            raise AttributeError(f"没有{ty}这个值")

